import timeimport jsonfrom .muxshield import TMuxShieldfrom .server import do_connection, start_server, get_clientdef main():    with open('../config.json', 'r') as f:        config = json.loads(f.readline())    essid = config['essid']    password = config['password']    ip = do_connection(essid, password)    print(f'Connected to {essid}, assigned IP: {ip}')    tmux = TMuxShield()    s = start_server()    while True:        cl_file = get_client(s)        try:            while True:                data = {}                for i in range(8):                    data[i+1] = tmux.read_temp(i)                json.dump(data, cl_file)                time.sleep(2)            print('Client disconnected')        except Exception as e:            print('Error exception: ', e)            cl_file.close()