import networkimport socketdef do_connection(essid, password):    wlan = network.WLAN(network.STA_IF)    wlan.active(True)    if not wlan.isconnected():        wlan.connect(essid, password)        while not wlan.isconnected():            pass    ip = wlan.ifconfig()[0]    return ipdef start_server(port=4050):    s = socket.socket()    addr = ('0.0.0.0', port)    s.bind(addr)    s.listen(1)    print(f'Server started on port: {port}')    return sdef get_client(s):    cl, addr = s.accept()    print(f'Client conneted: {addr}')    cl_file = cl.makefile('rwb', 0)    return cl_file  